import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Search from './Search';

class Header extends Component {
    state={
        getSearchData : []
    }

    searchData=(data)=>{
      
        this.setState({getSearchData : data});

    }

    render() {
        return (
            <header className="header">
            <div className="container">
                <div className="row">
                    <div className="col-lg-4 my-auto">
                        <a href="/" className="logo">PhotoShowcase</a>
                    </div>
                    <div className="col-lg-4 my-auto text-center">
                        <div className="mainmenu">
                            <ul>
                                <li><Link getdata={this.getSearchData} to='/'>Home </Link></li>
                                 <li><Link to='/about'>About </Link></li>
                                <li><Link to="/disclaimer">Disclaimer</Link></li>
                            </ul>
                        </div>
                    </div>
                    <div className="col-lg-4 my-auto text-right">
                        <Search ondata={this.searchData} />
                       
                    </div>
                </div>
            </div>

            <div className="row">
                    
                
                
                    {
                        this.state.getSearchData.map((photo) => (
                            <div key={photo.id} className="col-lg-3">
                                <div className="single-photo-item">
                                    <a className="d-block" href={'photo?id=' + photo.id}>
                                        <div className="photo-wrapper">
                                            <img src={photo.urls.small} alt={photo.description}/>
                                        </div>
                                        <h5>{photo.description}</h5>
                                        <p className="cat-name">by - {photo.user.first_name} {photo.user.last_name}</p>
                                    </a>
    
                                    <a className="dl-icon" rel="noopener noreferrer" title="Download" target="_blank" href={photo.links.download} download><img src={downloadIcon} alt="download"/></a>
                                </div>
                            </div>
                        ))
                    }
    
                    </div>


        </header>
        );
    }
}

export default Header;
