import React, { Component } from 'react';
import unsplash from '../unsplash'

class Photo extends Component {

    state = {
        photo: [],
        loading: true,
    }

    componentDidMount() {


        let search = window.location.search;
        let params = new URLSearchParams(search);
        let photo_id = params.get('id');
 
        unsplash.get('/photos/' ,{
            params :{

                id : photo_id

            }

            }).then((res)=>{

                    this.setState({photo:res.data.results});
                   
            })

    }


    render() {

        return (

            <div>
                {this.state.photodescription ? <h2 className="text-center">{this.state.photodescription}</h2> : ''}
                <div className="photo-single-wrapper">
                    <div className="photo-single-info">
                        
                        
                        
                        <ul>
                            <li><label htmlFor="uplaodedd_by">Uploaded by</label> {this.state.photouser && photo.user.first_name} {this.state.photouser && photo.user.last_name}</li>
                            
                            {this.state.photoupdated_at ? <li><label htmlFor="upload_date">Upload date</label> {this.state.photoupdated_at}</li> : ''}
                            
                            <li><label htmlFor="camera_model">Camera model</label> {this.state.photoexif && photo.exif.model}</li>
                            
                        </ul>

                        <a target="_blank" rel="noopener noreferrer" href={this.state.photolinks && photo.links.download} download>Download <img src={downloadIcon} alt="download"/></a>
                    </div>
                    <div className="photo-img-n"><img src={this.state.photourls && photo.urls.full} alt=""/> </div>
                </div>
            </div>
        );
    }
}

export default Photo;
