import React, { Component } from 'react';
import Search from '../Search';
import unsplash from '../unsplash'

class Home extends Component {

    state = {

        photos: [] ,

        page : 1,

    }
    searchData = (data) => {

        this.setState({ photos: data });

    }

    componentDidMount(e){

        unsplash.get('/photos' ,{
            params :{

                page :e ,
                per_page : 16 
            }

        }).then((res)=>{
                this.setState({

                    photos : res.data
                });               
        })

        window.scrollTo({
            top: 0,
            behavior: 'smooth',
        })
    };

    loadNextPage=()=>{
        console.log(this.state.page)

        this.setState({page:this.state.page+1});
        
        this.componentDidMount(this.state.page+1)

    }
    render() {
        return (
            <React.Fragment>
                <div className="container">
                    <div className="row top-heading">
                        <div className="col my-auto"></div>
                        <div className="col col-auto my-auto">
                            <div className="col-lg-4 my-auto text-right">
                                <Search ondata={this.searchData} />

                            </div>
                        </div>
                    </div>

                        
                    <h2>Latest Photos</h2>
                    <div className="row">
                        {
                            this.state.photos.map((photo) => (
                                <div key={photo.id} className="col-lg-3">
                                    <div className="single-photo-item">
                                        <a className="d-block" href={'photo?id=' + photo.id}>
                                            <div className="photo-wrapper">
                                                <img src={photo.urls.small} alt={photo.description} />
                                            </div>
                                            <h5>{photo.description}</h5>
                                            <p className="cat-name">by - {photo.user.first_name} {photo.user.last_name}</p>
                                        </a>

                                        {/* <a className="dl-icon" rel="noopener noreferrer" title="Download" target="_blank" href={photo.links.download} download><img src={downloadIcon} alt="download"/></a> */}
                                    </div>
                                </div>
                            ))
                        }

                    </div>


                    <div className="row">
                        <div className="col-lg-12 text-center">
                    <div className="load-more-btn"> <button className="btn btn-success" onClick={this.loadNextPage}>Load Page {this.state.page}</button></div>
                        </div>
                    </div>
                </div>
            </React.Fragment>

        );
    }
}

export default Home;
