import React, { Component } from 'react';
import unsplash from '../apis/unsplash/unsplash'


class Search extends Component {

    state={

        SearchValue : '' ,
        searchData : []
    }

    onChangeValue=(event)=>{

        this.setState({SearchValue:event.target.value}) ;

    }

    onSubmitForm=(event)=>{

        event.preventDefault();

        // this.props.onFormSubmit(this.state.SearchValue);

    unsplash.get('/search/photos' ,{
            params :{
                        query : event,
                    }

           }).then((res)=>{

                this.setState({searchData: res.data.results});
           })

    }
    render() {
        return (
            <form onSubmit={this.onSubmitForm}>
                <input onChange={this.onChangeValue} value={this.state.SearchValue} type="text" placeholder="Search keyword"/>
                <input  type="submit" value="search"/>
            </form>
        );
    }
}

export default Search;
